sys_script_fix.script:
// 固定IPアドレス管理
var ipMngGr = new GlideRecord('x_ritsc_a210002301_fixed_ip_address_management');
ipMngGr.query();
gs.info("start: IP Management record count=" + ipMngGr.getRowCount());
while (ipMngGr.next()) {
	if (ipMngGr.u_devive_auth == ""){
		if (ipMngGr.u_certificate_name != ""){
			ipMngGr.u_devive_auth = ipMngGr.u_certificate_name;
		}else{
			ipMngGr.u_devive_auth = ipMngGr.u_mac_address_name;
		}	
	}
}
